import { React, useState } from "react";
import TextField from "@mui/material/TextField";
// import List from "./Components/List"
import "./App.css";
import "./app.py";


function App() {

  const [query, setQuery] = useState([])
  const [answer, setAnswer] = useState("")

  // const [inputText, setInputText] = useState("");
  // let inputHandler = (e) => {
  //   var lowerCase = e.target.value.toLowerCase();
  //   setInputText(lowerCase);
  //   setQuery(lowerCase);
  //   const myObjStr = JSON.stringify(Object.assign({}, query));
  //   console.log(myObjStr);
    // myObjStr.json()
  const inputHandler = (e) => {
    const userInput = e.target.value;
    setQuery(userInput);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      // Si la touche "Entrée" est enfoncée, effectuez la recherche
      handleSubmit();
    }
  };

  const handleSubmit = () => {
    const jsonData = {
      query: query,
    };

    // Envoyez l'objet JSON au backend (vous pouvez utiliser fetch ou axios pour cela)
    fetch('http://localhost:3001/api/query', {
      method: 'POST', // Utilisez la méthode appropriée (POST, PUT, etc.) en fonction de votre backend
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(jsonData),
    })
      .then(response => response.json())
      .then(data => {
        // Traitez la réponse du backend si nécessaire
        console.log("message", data.message);
        console.log("answer", data.message.answer);
        setAnswer(data.message.answer);
      })
      .catch(error => {
        console.error('Erreur lors de l\'envoi de la requête au backend :', error);
      });
      console.log(jsonData);

  };
  return (
    <div className="main">
      <h1>Obstetric Search</h1>
      <div className="search">
        <TextField
          id="outlined-basic"
          onChange={inputHandler}
          onKeyDown={handleKeyDown} 
          variant="outlined" 
          fullWidth
          label="Search"
        />
      <button className="search-button" onClick={handleSubmit}>Submit</button>
      </div>
      {answer && (
      <div className="answer-container">
      <p className="answer-text">{answer}</p>
      </div>)}
    </div>
  );
}

export default App;
